export interface Message {
    id: string;
    typ: string;
    thid: string;
    type: string;
    body: unknown;
}
export interface AuthorizationRequestMessage {
    id: string;
    typ: string;
    type: string;
    thid: string;
    body: AuthorizationRequestBody;
    from: string;
    to?: string;
}
export interface AuthorizationResponseMessage {
    id: string;
    typ: string;
    type: string;
    thid: string;
    body: AuthorizationResponseBody;
    from: string;
    to: string;
}
export interface AuthorizationRequestBody {
    message?: string;
    reason: string;
    callbackUrl: string;
    scope: ZKPRequest[];
}
export interface AuthorizationResponseBody {
    message?: string;
    scope: ZKPResponse[];
}
export interface ProofData {
    pi_a: string[];
    pi_b: string[][];
    pi_c: string[];
    protocol?: string;
    curve?: string;
}
export interface ZKPRequest {
    id: number;
    circuit_id: string;
    optional?: boolean;
    rules: unknown;
}
export interface ZKPResponse {
    id: number;
    circuit_id: string;
    pub_signals: string[];
    proof: ProofData;
}
export interface Schema {
    hash?: string;
    url: string;
    type: string;
}
